function swap<T,U>(a:T,b:U) {
  return [b,a]
}
const [b,a] = swap('나야 들기름',2)

function returnFirstValue<T>(data:[T,...unknown[]]){
  return data[0]
}
let num1 = returnFirstValue(['집가고싶다',1,2])

function getLength<T extends{length:number}>(data:T){
  return data.length
}

